import React, { Fragment, useState } from "react";
import { Link, useNavigate  } from "react-router-dom";
import axios from 'axios';

function Register() {
    const [values, setValues] = useState({
        name: "",
        email: '',
        password: ''
    })
    const navigate = useNavigate()
    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post('http://localhost:5100/register', values)
        .then(res => {
            if(res.data.Status == 'Success') {
                navigate('/login')
            } else {
                alert()
            }
        })
        .then(err => console.log(err))
    }


    return (
        <Fragment>
            <div className="d-flex justify-content-center align-items-center bg-primary vh-100">
                <div className="bg-white p-3 rounded w-25">
                    <h2>Sign-up</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="name"><strong>Name</strong></label>
                            <input type="text" id="name" placeholder="Enter Name" name="name" 
                            onChange={e => setValues({...values, name:e.target.value})} className="form-control rounded-0" />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="email"><strong>Email</strong></label>
                            <input type="email" id="email" placeholder="Enter Email" name="email" 
                            onChange={e => setValues({...values, email:e.target.value})} className="form-control rounded-0" />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="password"><strong>Password</strong></label>
                            <input type="password" id="password" placeholder="Enter Password" 
                            onChange={e => setValues({...values, password:e.target.value})} name="password" className="form-control rounded-0" />
                        </div>

                        <button type="submit" className="btn btn-success w-100 rounded-0">Sign Up</button>
                        <p>You agree to our terms and policies</p>
                        <Link to='/login' className="btn btn-default border w-100 bg-light rounded-0 text-decoration-none">Login</Link>
                    </form>
                </div>
            </div>
        </Fragment>
    )
}

export default Register;
