import React, { useState, Fragment } from 'react';
import axios from "axios";
import { useNavigate } from 'react-router-dom';

function Login() {
  // State for form fields
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Email:", email, "Password:", password);
    // Add form validation or API calls here
  };

  return (
    <Fragment>
        <div className="container-fluid p-0">
            {/** Navigation bar */}
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container-fluid">
                    <a className="navbar-brand font-weight-bolder" href="#">Directorate of Technical Education</a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul className='navbar-nav ml-auto'>
                        <li className='nav-item'>
                        <a className='nav-link' href='#'>
                            <i className='fas fa-sign-out-alt'></i>Contact Us
                        </a>
                        </li>
                    </ul> 
                    </div>
                </div>
            </nav>

            <section className="vh-85 mt-5 d-flex justify-content-center align-items-center">
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-sm-6 text-black">
                            <div className="card shadow-lg p-4" style={{ borderRadius: '15px' }}>
                                <div className="d-flex align-items-center justify-content-center h-custom-2 px-5">
                                    <form style={{ width: '23rem' }} onSubmit={handleSubmit}>
                                        <h3 className="fw-normal mb-3 pb-3" style={{ letterSpacing: '1px' }}>Log in</h3>

                                        <div className="form-outline mb-4">
                                            <input
                                                type="email"
                                                id="form2Example18"
                                                className="form-control form-control-lg"
                                                value={email}
                                                onChange={(e) => setEmail(e.target.value)}
                                            />
                                            <label className="form-label" htmlFor="form2Example18">Email address</label>
                                        </div>

                                        <div className="form-outline mb-4">
                                            <input
                                                type="password"
                                                id="form2Example28"
                                                className="form-control form-control-lg"
                                                value={password}
                                                onChange={(e) => setPassword(e.target.value)}
                                            />
                                            <label className="form-label" htmlFor="form2Example28">Password</label>
                                        </div>

                                        <div className="pt-1 mb-4">
                                            <button className="btn btn-info btn-lg btn-block" type="submit">Login</button>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    </Fragment>
  );
}

export default Login;
